import React, { useState } from "react";

export default function Checkout() {
  const [userData, setUserData] = useState({
    name: "",
    street: "",
    postalCode: "",
    city: "",
  });
  const [error, seterror] = useState({
    nameErr: "",
    streetErr: "",
    postalCodeErr: "",
    cityErr: "",
    isvalidform:false
  });

  const onChangeHandler = (e) => {
    if (e.target.name === "name") {
      setUserData({
        ...userData,
        name: e.target.value,
      });
      seterror({
        ...error,
        nameErr: e.target.value.length == 0 ? "name shouldn't be empty":e.target.value.length < 3 && "must be at least 3 characters" ,
        isvalidform: error.nameErr &&true
      })
    } else if (e.target.name === "street") {
      setUserData({
        ...userData,
        street: e.target.value,
      });
    } else if (e.target.name === "postalCode") {
      setUserData({
        ...userData,
        postalCode: e.target.value,
      });
    } else if (e.target.name === "city") {
      setUserData({
        ...userData,
        city: e.target.value,
      });
    } else {
      return;
    }
  };

  const submitHandler=(e)=>{
    e.preventDefault()
    if (!error.isvalidform){
        console.log('success')
    }
  }
  return (
    <form onSubmit={submitHandler} className="row g-3">
      <div className="col-auto">
        <label for="Name">Your Name</label>
        <input
          onChange={onChangeHandler } 
          value={userData.name}
          type="text"
          name="name"
          className="form-control"
          id="Name"
          placeholder="Name"
        />
       <span className="text-danger">{error.nameErr}</span>
      </div>
      <div className="col-auto">
        <label for="Street">Street</label>
        <input
          onChange={onChangeHandler } 
          value={userData.street}
          name="street"
          type="text"
          className="form-control"
          id="Street"
          placeholder="Street"
        />
      </div>
      <div className="col-auto">
        <label for="Postal Code">Postal Code</label>
        <input
          onChange={onChangeHandler } 
          value={userData.postalCode}
          type="number"
          name="postalCode"
          className="form-control"
          id="Postal Code"
          placeholder="Postal Code"
        />
      </div>
      <div className="col-auto">
        <label for="City">City</label>
        <input
          onChange={onChangeHandler } 
          value={userData.city}
          name="city"
          type="text"
          className="form-control"
          id="City"
          placeholder="City"
        />
      </div>
      <div className="col-auto">
        <button type="submit" className="btn btn-primary mb-3">
          Order
        </button>
      </div>
      <div className="col-auto">
        <button type="submit" className="btn btn-primary mb-3">
          Cancel
        </button>
      </div>
    </form>
  );
 }
