import React, { useState } from "react";

export default function MealItemForm() {
  const [number, setnumber] = useState(0);
  const changeInputHandler = () => {
    setnumber((prevCount) => {
      return prevCount + 1;
    });
  };
  const SubmitHandler = (e) => {
    e.preventDefault();
  };
  return (
    <form onSubmit={SubmitHandler}>
      <div className="mb-3">
        <label for="exampleInputEmail1" className="form-label me-3">
         Amount 
        </label>
        <input onChange={changeInputHandler} min="1" max="5" defaultValue="1" style={{ width: "50px" }} type="number" value={number} />
        <button
          onClick={changeInputHandler}
          type="button"
          className="btn btn-primary ms-2"
        >
         Add
        </button>
      </div>
      {/* <button onClick={changeInputHandler}>{number}+</button> */}
    </form>
  );
}
