import React, { useState } from "react";
import Movie from "./Movie";

export default function ListMovies() {
    const [movies,setMovies]=useState([])
    async function fetchMoviesHandler(){
        const response = await fetch('https://swapi.dev/api/films/')
        const data = await response.json()
        setMovies(data.results)
    }


    // function fetchMoviesHandler(){
    //     fetch('https://swapi.dev/api/films/').then((response)=>{
    //     return response.json();
    //     }).then((data)=>{
    //         setMovies(data.results)
    //     })

    // }

  return (
    <div className="container text-center">
        <button className="btn btn-danger d-flex " onClick={fetchMoviesHandler}>fetch data</button>
      <div className="row">
        {
            movies.map((movie)=>{
                return <div className="col-lg-3 mt-2 ">
                    <Movie opening_crawl={movie.opening_crawl} release_date={movie.release_date} title={movie.title}/> 

                </div>
            })
        }
        <div className="col-lg-3">


        </div>
      </div>
    </div>
  );
}
